mapper.select:
SELECT USER_ID
	, CORP_CODE
	, USER_PW
	, USER_NAME
	, USER_PHONE
	, USER_EMAIL
	, USER_LEVEL
	, TOKEN
	, USER_DEPARTMENT
	, USER_PUSHID
	, USER_RANKCODE
	, USER_ROLE
	, USER_VALUE
	, USER_STATUS
	, SESSION_ID
	, PROFILE_NAME
FROM SEED_PEOPLE_HDR
WHERE 1=1
 AND USER_ID         = #{userId}        
 AND CORP_CODE       = #{corpCode}      
 AND USER_PW         = #{userPw}        
 AND USER_NAME       = #{userName}      
 AND USER_PHONE      = #{userPhone}     
 AND USER_EMAIL      = #{userEmail}     
 AND USER_LEVEL      = #{userLevel}     
 AND TOKEN           = #{token}         
 AND USER_DEPARTMENT = #{userDepartment}
 AND USER_PUSHID     = #{userPushid}    
 AND USER_RANKCODE   = #{userRankcode}  
 AND USER_ROLE       = #{userRole}      
 AND USER_VALUE      = #{userValue}     
 AND USER_STATUS     = #{userStatus}    
 AND SESSION_ID      = #{sessionId}     
 AND PROFILE_NAME    = #{profileName}

mapper.insert:
INSERT INTO SEED_PEOPLE_HDR (
	USER_ID, CORP_CODE
	 , USER_PW         
	 , USER_NAME       
	 , USER_PHONE      
	 , USER_EMAIL      
	 , USER_LEVEL      
	 , TOKEN           
	 , USER_DEPARTMENT 
	 , USER_PUSHID     
	 , USER_RANKCODE   
	 , USER_ROLE       
	 , USER_VALUE      
	 , USER_STATUS     
	 , SESSION_ID      
	 , PROFILE_NAME    
) VALUES(
	#{userId}, #{corpCode}
	 , #{userPw}         
	 , #{userName}       
	 , #{userPhone}      
	 , #{userEmail}      
	 , #{userLevel}      
	 , #{token}          
	 , #{userDepartment} 
	 , #{userPushid}     
	 , #{userRankcode}   
	 , #{userRole}       
	 , #{userValue}      
	 , #{userStatus}     
	 , #{sessionId}      
	 , #{profileName}    
)

mapper.update:
UPDATE SEED_PEOPLE_HDR SET UPDATE_DATE = NOW()
	 , CORP_CODE       = #{corpCode}       
	 , USER_PW         = #{userPw}         
	 , USER_NAME       = #{userName}       
	 , USER_PHONE      = #{userPhone}      
	 , USER_EMAIL      = #{userEmail}      
	 , USER_LEVEL      = #{userLevel}      
	 , TOKEN           = #{token}          
	 , USER_DEPARTMENT = #{userDepartment} 
	 , USER_PUSHID     = #{userPushid}     
	 , USER_RANKCODE   = #{userRankcode}   
	 , USER_ROLE       = #{userRole}       
	 , USER_VALUE      = #{userValue}      
	 , USER_STATUS     = #{userStatus}     
	 , SESSION_ID      = #{sessionId}      
	 , PROFILE_NAME    = #{profileName}    
WHERE USER_ID = #{userId}
